import React, { useState, useEffect } from 'react';
import MovieList from './components/MovieList';
import Filter from './components/Filter';

function App() {
  const [darkMode, setDarkMode] = useState(() =>
    window.matchMedia('(prefers-color-scheme: dark)').matches
  );

  const [movies, setMovies] = useState([
    {
      title: 'Inception',
      description: 'A mind-bending thriller.',
      posterURL: 'https://m.media-amazon.com/images/I/81p+xe8cbnL._AC_SY679_.jpg',
      rating: 8.8,
    },
    {
      title: 'Interstellar',
      description: 'Space and time exploration.',
      posterURL: 'https://m.media-amazon.com/images/I/71y5f+V94qL._AC_SY679_.jpg',
      rating: 8.6,
    },
  ]);

  const [titleFilter, setTitleFilter] = useState('');
  const [ratingFilter, setRatingFilter] = useState('');
  const [newMovie, setNewMovie] = useState({
    title: '',
    description: '',
    posterURL: '',
    rating: '',
  });

  useEffect(() => {
    document.documentElement.classList.toggle('dark', darkMode);
  }, [darkMode]);

  const handleAddMovie = () => {
    if (
      newMovie.title.trim() &&
      newMovie.description.trim() &&
      newMovie.posterURL.trim() &&
      !isNaN(newMovie.rating)
    ) {
      setMovies([...movies, { ...newMovie, rating: parseFloat(newMovie.rating) }]);
      setNewMovie({ title: '', description: '', posterURL: '', rating: '' });
    }
  };

  const filteredMovies = movies.filter((movie) => {
    return (
      movie.title.toLowerCase().includes(titleFilter.toLowerCase()) &&
      (!ratingFilter || movie.rating >= parseFloat(ratingFilter))
    );
  });

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-gray-900 dark:text-white transition-colors px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">ðŸŽ¬ Movie App</h1>
        <button
          onClick={() => setDarkMode(!darkMode)}
          className="px-4 py-2 bg-gray-200 dark:bg-gray-700 rounded"
        >
          Toggle {darkMode ? 'Light' : 'Dark'} Mode
        </button>
      </div>

      <Filter
        titleFilter={titleFilter}
        setTitleFilter={setTitleFilter}
        ratingFilter={ratingFilter}
        setRatingFilter={setRatingFilter}
      />

      <h2 className="text-xl font-semibold mb-2">Add a Movie</h2>
      <div className="flex flex-col md:flex-row gap-2 mb-4">
        <input
          className="p-2 border rounded dark:bg-gray-800"
          placeholder="Title"
          value={newMovie.title}
          onChange={(e) => setNewMovie({ ...newMovie, title: e.target.value })}
        />
        <input
          className="p-2 border rounded dark:bg-gray-800"
          placeholder="Description"
          value={newMovie.description}
          onChange={(e) => setNewMovie({ ...newMovie, description: e.target.value })}
        />
        <input
          className="p-2 border rounded dark:bg-gray-800"
          placeholder="Poster URL"
          value={newMovie.posterURL}
          onChange={(e) => setNewMovie({ ...newMovie, posterURL: e.target.value })}
        />
        <input
          className="p-2 border rounded dark:bg-gray-800"
          placeholder="Rating"
          type="number"
          value={newMovie.rating}
          onChange={(e) => setNewMovie({ ...newMovie, rating: e.target.value })}
        />
        <button
          onClick={handleAddMovie}
          className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
        >
          Add
        </button>
      </div>

      <MovieList movies={filteredMovies} />
    </div>
  );
}

export default App;
